// define a variable to import the <Verifier> or <renamedVerifier> solidity contract generated by Zokrates
let squareVerifier = artifacts.require('Verifier');
// Test verification with correct proof
let zokratesProof = require('../../zokrates/code/square/proof');
// - use the contents from proof.json generated from zokrates steps
contract('TestSquareVerifier', accounts => {

    const account_1 = accounts[0];

    describe('Test verification with correct proof', function () {
        beforeEach(async function () {

            try {
                this.contract = await squareVerifier.new({ from: account_1 });
            }
            catch (e) {
                console.log(e);
            }
        });

        it('should verify true with the correct proof', async function () {
            let result = await this.contract.verifyTx.call(
                zokratesProof.proof.a,
                zokratesProof.proof.b,
                zokratesProof.proof.c,
                zokratesProof.inputs,
                { from: account_1 }
            );

            assert.equal(result, true, "Unexpected verification result");
        });

        // Test verification with incorrect proof
        it('should verify false with the incorrect proof', async function () {
            let inputs = [10, 2]
            let result = await this.contract.verifyTx.call(
                zokratesProof.proof.a,
                zokratesProof.proof.b,
                zokratesProof.proof.c,
                inputs,
                { from: account_1 }
            );

            assert.equal(result, false, "The proof is not correct");
        })
    })
});
